package com.loop;

public class Ques11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=5;
		switch(number%2) {
		case 0:
			System.out.println("Number is even");
			break;
		case 1:
			System.out.println("Number is odd");
			break;
	    default:
	    	System.out.println("Invalid Number");
	    	break;
		}

	}

}
